import React from "react"
import Rectangle from "react-rectangle";
import {useState} from "react";
import itemStyle from "./ArrayStyle.css"
var array;
function ArraySorter()
{
    array = new ArrayModel();
    const [data, setData] = useState(typeof(array) == "null" ? new ArrayModel() : array);

    function plus() {
        data.data.push(rectangle(5, 600));
        setData(data);
    }

    return(
        <div>
            <ul>
                {data.data}
            </ul>
            <button onClick={setData(rectangle(5,100))}>Add a Rectangle</button>
        </div>

    );
}
class ArrayModel extends React.Component
{
    constructor()
    {
        super();
        this.data = listOfRectangles(5, 600, 10);
    }
    render()
    {
        return (this.data);
    }

    change(array, x, y)
    {
        let tmp = this.data[x];
        this.data[x] = this.data[y];
        this.data[y] = tmp;
        return this.data;
    }
}
function listOfRectangles(x, y, num)
{
    let array = [];
    for(let i = 0; i < num; i++)
    {
        array.push(
            <li style={{float: "Left", background: '#9e9e9e', width: x, height: y }}>{rectangle(x,randomNum(0, y))}</li>
        );
    }
    return (array);



}
function rectangle(x, y)
{
    return (
        <Rectangle aspectRatio={[5, 3]}>
            <div style={{ background: '#607d8b', width: '100%', height: '100%' }} />
        </Rectangle>
    );
}
function randomNum(min, max)
{
    return (Math.random() * max) + min;
}
export default ArraySorter;
