import React from "react"
import Rectangle from "react-rectangle";
import {useState} from "react";
import itemStyle from "./ArrayStyle.css"
var array;
function ArraySorter()
{

    const [data, setData] = useState(null);
    if(array == null)
    {
        array = new ArrayModel();
        setData(array);
    }
    function plus() {
        array.add(listOfRectangles(5, 600, 1));
        setData(array);
    }
    console.log(array);
    return(
        <div>
            <ul>
                {array.data}
            </ul>
        </div>

    );
}
class ArrayModel extends React.Component
{
    constructor()
    {
        super();
        this.data = listOfRectangles(5, 600, 10);
    }
    add(element)
    {
        this.data.push(element);
    }
    render()
    {
        return (this.data);
    }

    change(array, x, y)
    {
        let tmp = this.data[x];
        this.data[x] = this.data[y];
        this.data[y] = tmp;
        return this.data;
    }
}
function listOfRectangles(x, y, num)
{
    let array = [];
    for(let i = 0; i < num; i++)
    {
        array.push(
            <li style={{float: "Left"}}>{rectangle(x,randomNum(0, y))}</li>
        );
    }
    return (array);



}
function rectangle(x, y)
{
    return (
        <div style={{ background: '#9e9e9e', width: x, height: y }}>
            <Rectangle aspectRatio={[5, 3]}>
                <div style={{ background: '#607d8b', width: '100%', height: '100%' }} />
            </Rectangle>
        </div>
    );
}
function randomNum(min, max)
{
    return (Math.random() * max) + min;
}
export default ArraySorter;
